
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 1;
		int j = 60;
		
		System.out.println("I=" + i + " " + "J=" + j);
		
		while(j > 0 && j <= 60) {
			i += 3;
			j = j - 5;
			
			System.out.println("I=" + i + " " + "J=" + j);
			
		}
		
	}

}
